import { CommonModule } from '@angular/common';
import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'storybook-dropdown',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="dropdown-container" *ngIf="isOpen">
      <div class="dropdown-list">
        <div 
          *ngFor="let item of items; trackBy: trackByFn" 
          class="dropdown-item"
          (click)="selectItem(item)"
          [class.selected]="item === selectedItem">
          {{ item }}
        </div>
      </div>
    </div>
  `,
  styleUrls: ['./dropdown.component.css'],
})
export class DropdownComponent {
  /** Liste des √©l√©ments √† afficher */
  @Input()
  items: string[] = [];

  /** √âtat ouvert/ferm√© */
  @Input()
  isOpen: boolean = false;

  /** √âl√©ment s√©lectionn√© */
  @Input()
  selectedItem?: string;

  /** √âv√©nement quand un √©l√©ment est s√©lectionn√© */
  @Output()
  itemSelected = new EventEmitter<string>();

  /** √âv√©nement pour fermer le dropdown */
  @Output()
  closeDropdown = new EventEmitter<void>();

  selectItem(item: string): void {
    this.selectedItem = item;
    this.itemSelected.emit(item);
    this.closeDropdown.emit();
  }

  trackByFn(index: number, item: string): string {
    return item;
  }
}

